import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import api from "./utils/api.js";
import UsersTable from "./components/UsersTable";
import FilterInput from "./components/FilterInput";
import { Container, Jumbotron } from "react-bootstrap";
import { InputGroup, FormControl } from "react-bootstrap";
import { Table, Button, Figure } from "react-bootstrap";

const App = () => {
  const [users, setUsers] = useState([]);
  const [inputValue, setInputValue] = useState("");

  useEffect(() => {
    const fetchUsers = async () => {
      const data = await api.users();

      setUsers(data);
    };

    fetchUsers();
  }, []);

  const handleSortAscending = () => {
    const sortedUsers = [...users].sort((a, b) => (a.login > b.login ? 1 : -1));

    setUsers(sortedUsers);
  };

  const handleSortDescending = () => {
    const sortedUsers = [...users].sort((a, b) => (a.login > b.login ? -1 : 1));

    setUsers(sortedUsers);
  };

  return (
    <Container>
      <Jumbotron>
        <h1>User Directory</h1>
      </Jumbotron>
      <InputGroup className="mb-3">
      <InputGroup.Prepend>
        <InputGroup.Text id="basic-addon1">Filter results: </InputGroup.Text>
      </InputGroup.Prepend>
      <FormControl
        value={inputValue}
        onChange={event => setInputValue(event.target.value)}
        placeholder="username"
        aria-label="username"
        aria-describedby="basic-addon1"
      />
    </InputGroup>






      <Table striped bordered hover>
      <thead>
        <tr>
          <th>avatar</th>
          <th>
            username{"  "}
            <Button onClick={handleSortAscending} variant="secondary" size="sm">
              <i className="fa fa-chevron-down"></i>
            </Button>{" "}
            <Button
              onClick={handleSortDescending}
              variant="secondary"
              size="sm"
            >
              <i className="fa fa-chevron-up"></i>
            </Button>
          </th>
          <th>url</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user.id}>
            <td>
              <Figure.Image
                width={80}
                height={80}
                alt={user.login}
                src={user.avatar_url}
              />
            </td>
            <td>{user.login}</td>
            <td>
              <a href={user.html_url}>GitHub</a>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>



{/*       
      <UsersTable
        handleSortAscending={handleSortAscending}
        handleSortDescending={handleSortDescending}
        users={users.filter(({ login }) => login.includes(inputValue))}
      /> */}
    </Container>
  );
};

export default App;
